import React, { Component } from 'react';

import ToDo from './ToDo.js';

class Nontification extends Component {
  constructor(props) {
    super(props);
    this.state = {
        todos: [
          { id: 1, description: 'Walk the cat', isCompleted: true },
          { id: 2, description: 'Throw the dishes away', isCompleted: false },
          { id: 3, description: 'Buy new dishes', isCompleted: false}
        ],
        newTodoDescription: ''
      };
    this.deleteTodo = this.deleteTodo.bind(this);
  }

handleChange(e) {
  this.setState({ newTodoDescription: e.target.value })
}

handleSubmit(e) {
  e.preventDefault();
  if (!this.state.newTodoDescription) { return }
  const newTodo = { description: this.state.newTodoDescription, isCompleted: false };
  this.setState({ todos: [...this.state.todos, newTodo], newTodoDescription: '' });
}


toggleComplete(index) {
  const todos = this.state.todos.slice();
  const todo = todos[index];
  todo.isCompleted = todo.isCompleted ? false : true;
  this.setState({ todos: todos });
}


deleteTodo(id) {
    this.setState((prevState) => ({
        todos: prevState.todos.filter(item => item.id !== id),
    }))
};


  render() {
    return (
      <div className="App">
        <form onSubmit={ (e) => this.handleSubmit(e)}>
          <input type="text"
            value={ this.state.newTodoDescription }
            onChange={ (e) => this.handleChange(e) }
            />
          <input type="submit" />
        </form>
        <ul>
          { this.state.todos.map( (todo, index) =>
            <ToDo key={ index }
              description={ todo.description }
              isCompleted={ todo.isCompleted }
              toggleComplete={ () => this.toggleComplete(index) }
              onDelete={ this.deleteTodo }
               />
          )}

        </ul>
      </div>
    );
  }
}

export default Nontification;
